			if(!link[i]||find(link[i])){ //若i已经配对，则查找和i配对的那个元素是否还能和其他元素配对
				link[i]=a; //若可以则把i配给a
				return 1;
			}
		}
	}
	return 0;
}
int judge()        //判断是否是二分图
{
	int i,cur;
	queueq;       //队列声明
	q.push(1);       //把1加入队列
	while(!q.empty()){
		cur=q.front();      //取队首元素
		q.pop();             //删除队首元素
		for(i=1;i<=n;i++){
			if(map[cur][i]){      //1和2、3认识则把2、3均标记为2；  
				if(color[i]==-1){
					color[i]=1-color[cur];
					q.push(i);
				}
				else if(color[i]==color[cur])      //接下来到2出对时，color[3]=color
					return 0;
			}
		}
	}
	return 1;
}
int main()
{
	int i,j,m,ans;
	while(scanf("%d%d",&n,&m)!=-1){
		memset(link,0,sizeof(link));
		memset(map,0,sizeof(map));
		memset(color,-1,sizeof(color));
		while(m--){
			scanf("%d%d",&i,&j);
			map[i][j]=map[j][i]=1;
		}
		if(judge()==0){
			printf("No\n");
			continue;
		}
		ans=0;
		for(i=1;i<=n;i++){
			memset(mark,0,sizeof(mark));
			ans+=find(i);
		}
		printf("%d\n",ans/2);
	}
	return 0;
}
